import React from "react";
import SearchArea from "./components/SearchArea";
import PlayerCard from "./components/PlayerCard";
import "./App.css";

function App() {
  const [searchResult, setSearchResult] = React.useState("");
  const [inputText, setInputText] = React.useState("");

  React.useEffect(() => {
    const url = `https://api-football-v1.p.rapidapi.com/v2/players/search/${inputText}`;
    // const accessHost = process.env.REACT_APP_FOOTBALL_HOST_KEY;
    // const accessToken = process.env.REACT_APP_FOOTBALL_API_KEY;

    if (!inputText) {
      return;
    }

    fetch(url, {
      method: "GET",
      headers: {
        "x-rapidapi-host": "api-football-v1.p.rapidapi.com",
        "x-rapidapi-key": "3ab6deee7emsh797e9c862fb49b2p16f155jsn63530ff116f9",
      },
    })
      .then((res) => res.json())
      .then((json) => {
        setSearchResult(json.api.players[0]);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [inputText]);

  return (
    <div className="App">
      <h1>⚽ Football Aspects ⚽</h1>
      <SearchArea handleChange={(e) => setInputText(e.target.value)} />
      {searchResult && (
        <PlayerCard
          key={searchResult}
          Player={searchResult.player_name}
          Country={searchResult.birth_country}
        />
      )}
    </div>
  );
}

export default App;
